import cx from 'classnames'

import type { AIKIDOKA_TYPE, DanType } from '@/lib/api/aikidoka'
import { getFeaturedAkidokas } from '@/lib/api/aikidoka'

const layoutInnerClassName = 'mx-auto w-full max-w-7xl px-3'

export default async function Home() {
  const isPreview = process.env.NODE_ENV === 'development'
  const aikidokas =
    ((await getFeaturedAkidokas(isPreview)) as [AIKIDOKA_TYPE]) ?? []

  return (
    <>
      <header className='flex h-16 items-center border-b-2 border-stone-800'>
        <div className={layoutInnerClassName}>
          <div className='flex items-center gap-2'>
            <Aikido className='h-8' />
            <span className='text-2xl font-medium'>AIKIDŌ Morocco</span>
          </div>
        </div>
      </header>

      <main>
        <div className={layoutInnerClassName}>
          <Section>
            <SectionHeader className='mb-5'>
              AIKIDOKA of Morocco (5th dan and up)
            </SectionHeader>

            <section className='grid gap-4 md:grid-cols-2 xl:grid-cols-3'>
              {aikidokas.map((aikidoka) => {
                const filterDans = (dan: DanType) => !!dan.name

                const mDans =
                  aikidoka?.dansCollection?.moroccoDan?.filter(filterDans)
                const aDans =
                  aikidoka?.dansCollection?.aikikaiDan?.filter(filterDans)

                const dans = []
                if (mDans?.[0]?.name) {
                  dans.push(`${mDans?.[0]?.name} national`)
                }
                if (aDans?.[0]?.name) {
                  dans.push(`${aDans?.[0]?.name} AIKIKAI`)
                }

                return (
                  <article
                    key={aikidoka.sys.id}
                    className='flex border-2 border-stone-800'
                  >
                    {aikidoka.avatar.url ? (
                      <picture className='h-36 w-36 flex-shrink-0 border-r-2 border-stone-800 p-4 md:h-48 md:w-48'>
                        <source
                          srcSet={`${aikidoka.avatar.url}?w=192&h=192&fm=webp`}
                          type='image/webp'
                        />
                        <img
                          src={`${aikidoka.avatar.url}?w=192&h=192`}
                          alt=''
                        />
                      </picture>
                    ) : (
                      <div className='h-48 w-48 border-r-2 border-stone-800 p-4'>
                        <div className='h-full w-full bg-stone-600' />
                      </div>
                    )}

                    <div className='flex-grow p-4'>
                      <h4 className='mb-3 text-lg font-bold'>
                        {aikidoka.name}
                      </h4>
                      {dans.map((dan) => {
                        return <p key={dan}>{dan}</p>
                      })}
                    </div>
                  </article>
                )
              })}
            </section>
          </Section>

          <Section>
            <SectionHeader className='mb-5'>Stage/Agenda</SectionHeader>

            <section className='grid gap-4 md:grid-cols-3'></section>
          </Section>

          <Section>
            <SectionHeader className='mb-5'>Club/Dojo</SectionHeader>

            <section className='grid gap-4 md:grid-cols-3'></section>
          </Section>
        </div>
      </main>

      <footer>
        <div className={cx(layoutInnerClassName, 'py-8')}>
          <Logo size='sm' className='text-stone-500' />
        </div>
      </footer>
    </>
  )
}

function Section({ children }: { children: React.ReactNode }) {
  return <section className='py-8'>{children}</section>
}

function SectionHeader({
  children,
  className,
}: {
  children: React.ReactNode
  className?: string
}) {
  return <h2 className={cx('text-xl font-medium', className)}>{children}</h2>
}

function Logo({
  size = 'md',
  className,
}: {
  size?: 'sm' | 'md' | 'lg'
  className?: string
}) {
  const ss = {
    sm: 'text-lg',
    md: 'text-2xl',
    lg: 'text-3xl',
  }

  const gs = {
    sm: 'gap-1.5',
    md: 'gap-2',
    lg: 'gap-3',
  }

  return (
    <div className={cx('flex items-center', gs[size], className)}>
      <Aikido className='h-5' />
      <span className={ss[size]}>AIKIDŌ Morocco</span>
    </div>
  )
}

